/*********************************************************************************************
* Portal.Waiting (Warte oder Ladeazeig)
* Schuldigi: RL
* Kompatibilität: IE9, IE11, Chrome (!IE8)
* Abhängigkaite:
*     - Debug.js (optional)
*********************************************************************************************/

/*********************************************************************************************
* Changes:
*  - Standard Logs sind jez im Trace
*  - <Waiting> passt sich jezt de Phälter grössi automatisch ah
*  - Bilder sind jez als base64 drinne (kai pfad und weniger connections)
*  - StartAndStop()
*********************************************************************************************/

//todo: :after instead
//todo: base64 to use in other projects and avoid src issues..

; (function (ns)
{
    'use strict';

    ns.Waiting = {
        _Inline: ['select', 'input'], //Die Element chönd ned Phälter für divs sii

        _Images: [
            'data:image/gif;base64,',
            'data:image/gif;base64,',
            'data:image/gif;base64,'
        ],

        //_Path: function(){
        //    return (typeof window.top.Settings !== 'undefined' ? window.top.Settings.rootLink : '../') + 'images/Waiting/'
        //},

        //c:=<container>, e:=<waiting>
        _Clip: function (c, e)
        {
            this._Trace('_Clip', arguments);

            if (c && e)
            {
                var tB = c.getBoundingClientRect();

                if (this._Inline.indexOf(c.tagName.toLowerCase()) !== -1)
                {
                    e.style.left = tB.left + 'px';
                    e.style.top = tB.top + 'px';
                    e.style.width = tB.width + 'px';
                    e.style.height = tB.height + 'px';
                    e.style.right = e.style.bottom = null;
                    c.parentNode.appendChild(e);
                }
                //https://stackoverflow.com/questions/46127213/how-to-fix-typeerror-window-getcomputedstyle-is-null-in-firefox
                else if (window.getComputedStyle(c, null) && window.getComputedStyle(c, null).getPropertyValue('display') === 'inline')
                {
                    e.style.width = tB.width + 'px';
                    e.style.height = tB.height + 'px';
                }

                var tS = Math.min(48, tB.width, tB.height);
                e.style.backgroundSize = tS + 'px ' + tS + 'px';
            }
        },

        //c:=<Container>
        _Create: function (c)
        {
            this._Trace('_Create', arguments);

            if (c)
            {
                var tTimestamp = (c.waitStamp || new Date().getTime().toString());

                var tE = c.querySelector('#_' + tTimestamp);
                if (!tE)
                {
                    tE = document.createElement('div');
                    tE.id = '_' + tTimestamp;
                    tE.className = '_Waiting _resWaiting';
                    tE.style.backgroundRepeat = 'no-repeat';
                    tE.style.backgroundPosition = '50% 50%';
                    tE.style.backgroundImage = 'url(' + this._Images[0] + ')';
                    tE.style.bottom = '0';
                    tE.style.height = '100%';
                    tE.style.left = '0';
                    tE.style.margin = '0';
                    tE.style.position = 'absolute';
                    tE.style.right = '0';
                    tE.style.top = '0';
                    tE.style.width = '100%';
                    tE.style.zIndex = '2000000001';

                    tE.oncontextmenu = function () { return false; };

                    c.waitStamp = tTimestamp;
                    c.appendChild(tE);

                    this._setElementPosition(c);
                    this._Clip(c, tE);
                }

                return tE;
            }
        },

        //e:=<Waiting>
        _Expire: function (e)
        {
            this._Trace('_Expire', arguments);

            if (e)
            {
                e.style.backgroundImage = 'url(' + this._Images[2] + ')'; //http://www.loadinfo.net/
                e.className = '_Waiting _resWaitingExpired';
                e.title = 'Die Anzeige kann per Doppelklick beendet werden';

                e.ondblclick = e.ondblclick || function () { this.parentNode.removeChild(this); };
            }
        },

        //c:=<Container>
        _getWaiting: function (c)
        {
            this._Trace('_getWaiting', arguments);

            return c && c.waitStamp && c.querySelector('#_' + c.waitStamp);
        },

        //o:=object to log
        _Log: function (o)
        {
            (typeof Portal.Debug === 'object') ? Portal.Debug.Throw('Waiting', o) : (typeof console === 'object' && typeof console.log === 'function') && console.log(o);
        },

        //e:=<element>
        _setElementPosition: function (e)
        {
            this._Trace('_setElementPosition', arguments);

            if (e)
            {
                //https://stackoverflow.com/questions/46127213/how-to-fix-typeerror-window-getcomputedstyle-is-null-in-firefox
                var tS = window.getComputedStyle(e, null) ? window.getComputedStyle(e, null).getPropertyValue('position') : 'relative';
                e.style.position = ((tS === 'static') ? 'relative' : e.style.position);
            }
        },

        //o:=object
        _toObject: function (o)
        {
            var tO = (o instanceof HTMLElement || o instanceof SVGElement) ? { container: o } : o || { container: document.body };
            tO.container = tO.container || document.body;

            return tO;
        },

        //m:=message, a:=arguments
        _Trace: function (m, a)
        {
            //(typeof Portal.Debug !== 'undefined') && Portal.Debug.Trace('Portal.Waiting.' + m, a);
            //(typeof window.top.Portal.Debug !== 'undefined') && window.top.Portal.Debug.Trace('Portal.Waiting.' + m, a);
            (!!window.top.Portal && !!window.top.Portal.Debug) && window.top.Portal.Debug.Trace('Portal.Waiting.' + m, a);
        },

        //o:={object}
        Create: function (o)
        {
            this._Trace('Create', arguments);

            var tE = this._Create(o.container);
            o.title && (tE.title = o.title);

            return tE;
        },

        //c:=<Container>
        Error: function (c, m)
        {
            this._Trace('Error', arguments);

            var tE = this.Create(this._toObject(c));
            if (tE)
            {
                tE.style.backgroundImage = 'url(' + this._Images[1] + ')'; //http://www.loadinfo.net/
                tE.className = '_Waiting _resWaitingError';
                tE.title = (m || 'Die Anzeige kann per Doppelklick beendet werden');

                tE.ondblclick = tE.ondblclick || function () { this.parentNode.removeChild(this); };
            }
        },

        //c:=<Container> || {object}
        Start: function (c)
        {
            this._Trace('Start', arguments);

            var tO = this._toObject(c);
            var tE = this.Create(tO);

            if (tE)
            {
                tE.waitingCounter = (tE.waitingCounter || 0) + 1;

                tE.waitingTimer && clearTimeout(tE.waitingTimer);
                tE.waitingTimer = setTimeout(function (e)
                {
                    Portal.Waiting._Expire(e);
                }.bind(undefined, tE), 60000);
            }

            //REM: Browser hend schinbar mängisch azeige problem ohni timeout
            if (typeof tO.afterStartBegin === 'function')
            {
                window.setTimeout(function ()
                {
                    this.afterStartBegin(this);
                    (typeof this.afterStartEnd === 'function') && this.afterStartEnd(this);
                }.bind(tO), 10);
                //}.bind(tO), 4)
            }
        },

        //f:=afterStartBegin(), c:=<Container> || {object}
        StartAndStop: function (f, c)
        {
            this._Trace('StartAndStop', arguments);

            var tO = this._toObject(c);
            tO.afterStartBegin = f || afterStartBegin;
            tO.afterStartEnd = function () { Portal.Waiting.Stop(this); }.bind(tO);

            this.Start(tO);
        },

        //c:=<Container> || {object}
        Stop: function (c)
        {
            this._Trace('Stop', arguments);

            var tE = this._getWaiting(this._toObject(c).container); // this.Create(this._toObject(c));
            if (tE)
            {
                tE.waitingCounter = (tE.waitingCounter || 0) - 1;
                if (tE.waitingCounter < 1)
                {
                    tE.waitingTimer && clearTimeout(tE.waitingTimer);
                    tE.parentNode.removeChild(tE);
                }
            }
        },

        //c:=<Container> || {object}
        stopEntirely: function (c)
        {
            this._Trace('stopEntirely', arguments);

            var tE = this._getWaiting(this._toObject(c).container); // this.Create(this._toObject(c));
            if (tE)
            {
                tE.waitingCounter = 0;
                tE.waitingTimer && clearTimeout(tE.waitingTimer);
                tE.parentNode.removeChild(tE);
            }
        }
    };
}(window.Portal = window.Portal || {}));
